import React, { useState } from 'react';
import { findPageByUrl, getWordPercentageInPage } from '../api/auth';
import Button from '../components/Button';
import Input from '../components/Input';

const WordPercentagePage: React.FC = () => {
    const [input, setInput] = useState<string>('');
    const [word, setWord] = useState<string>('');
    const [pageId, setPageId] = useState<number | null>(null);
    const [percentageResults, setPercentageResults] = useState([]);
    const [error, setError] = useState<string>('');

    // Función para buscar el ID de la página usando el URL
    const handleFindPageId = async () => {
        try {
            const response = await findPageByUrl(input);
            setPageId(response.data.pageId);
            setError('');
        } catch (err) {
            setError('No se pudo encontrar el ID de la página con el URL proporcionado.');
        }
    };

    // Función para obtener el porcentaje de la palabra en la página
    const handleGetWordPercentage = async () => {
        if (!pageId || !word) {
            setError('Primero busca el ID de la página o ingresa una palabra válida.');
            return;
        }

        try {
            const response = await getWordPercentageInPage(pageId, word);
            setPercentageResults(response.data);
            setError('');
        } catch (err) {
            setError('No se pudo obtener el porcentaje de la palabra en la página.');
        }
    };

    return (
        <div className="text-white bg-zinc-900 m-auto h-screen max-w-[800px]">
            <h1 className="text-5xl p-5">Porcentaje de Palabra en Página</h1>

            {/* Input para URL o ID */}
            <div className="flex p-5">
                <Input
                    placeholder="Ingresa URL o ID de página..."
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                />
                <Button onClick={handleFindPageId}>Buscar ID</Button>
            </div>

            {/* Input para la palabra */}
            <div className="flex p-5">
                <Input
                    placeholder="Ingresa la palabra a buscar..."
                    value={word}
                    onChange={(e) => setWord(e.target.value)}
                />
                <Button onClick={handleGetWordPercentage}>Buscar Porcentaje</Button>
            </div>

            {/* Mensaje de error */}
            {error && <p className="text-red-500">{error}</p>}

            {/* Resultados */}
            <div className="p-5">
                <h2 className="text-3xl">Resultados</h2>
                <ul>
                    {percentageResults.map((result: any, index: number) => (
                        <li key={index}>
                            {result.palabra}: {result.cantidad} apariciones, {result.porcentaje}% del total de palabras
                        </li>
                    ))}
                </ul>
            </div>
        </div>
    );
};

export default WordPercentagePage;
