import React, { useEffect, useState } from 'react';
import {set, useForm} from 'react-hook-form';
import Button from '../components/Button';
import Input from '../components/Input';
import {useNavigate} from 'react-router-dom';
import {searchByWords} from '../api/auth';

const SearchWordsPage = () => {

  const navigate = useNavigate();
  const [links, setLinks] = useState<any>([]);
  const { register, handleSubmit, formState: {
    errors,
} } = useForm<FormData>();

  type FormData = {
    text: string;
  }

  const handleSearch = handleSubmit( async (data: FormData) => {
    try {
      const excludedWords: Set<string> = new Set([
        "el", "la", "lo", "los", "las", "en", "un", "una", "unos", "unas", "a", "ante", "bajo",
        "cabe", "con", "contra", "de", "desde", "entre", "hacia", "hasta", "para", "por", "según", 
        "sin", "so", "sobre", "tras", "y", "o", "u", "es", "son", "ser", "se", "como", "de", "ha", 
        "que", "del", "su", "#", "##", "###", "####", "#####", "######"
      ]);
      let words = data.text.split(/\s+/);
      words = words.filter(word => !excludedWords.has(word.toLowerCase()));

      words = Array.from(new Set(words));

      const response = await searchByWords();
      setLinks(response.data);
    } catch (err: any) {
      console.log(err.message)
    }
  });

  return (
    <div className="text-white bg-zinc-900 m-auto h-screen max-w-[800px]">
      <h1 className='text-5xl p-5'>Buscar por páginas</h1>
      <form onSubmit={handleSearch}>
        <div className='flex p-5'>
          <Input placeholder='Buscar palabras...' {...register("text", {required: true})}/>
          <Button type="submit" className="inline"> Search </Button>
        </div>
      </form>
      <Button className="text-2xl" onClick={() => {navigate("/")}}>Volver a inicio</Button>
      {links &&( 
        <div className="max-h-96 overflow-y-auto">
          <ul role="list" >
          {links.map((link: any, index: any) => (
            <li key={index} className="bg-white border border-gray-300 rounded-lg p-4 shadow-md mb-4">
                <h2 className="text-xl font-semibold text-gray-700">{link.word}</h2>
                <p className="text-gray-700">{link.url}</p>
            </li>
          ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default SearchWordsPage;
